mapper.select:
SELECT '延误率' AS rate_flag, Round(a.dly_rte*100,2) AS interest_rate, Round((a.dly_rte-b.dly_rte)/b.dly_rte*100,2) AS day_percent, Round((a.dly_rte-c.dly_rte)/c.dly_rte*100,2) AS week_with_percent, Round(b.dly_rte*100,2) AS day_time, Round(d.dly_snd_cnt,2) AS week_num, Round(e.dly_snd_cnt,2) AS month_num, Round(d.dly_rte*100,2) AS week_rate, Round(e.dly_rte*100,2) AS month_rate FROM ( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.grtr_dist_cd when #{sel_level} = '2' then o.prov_cd when #{sel_level} = '5' then o.org_cd else '000000' end AS dbct_cd, SUM(a.dly_snd_cnt)/SUM(a.dly_tot_cnt) AS dly_rte FROM ydbi.bas_s_bigdata_qut_ctl a LEFT JOIN ydbi.org_inf o ON a.dbct_cd = o.org_cd
AND a.stats_dt=#{data_date}
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
)a LEFT JOIN( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.grtr_dist_cd when #{sel_level} = '2' then o.prov_cd when #{sel_level} = '5' then o.org_cd else '000000' end AS dbct_cd, SUM(a.dly_snd_cnt)/SUM(a.dly_tot_cnt) AS dly_rte FROM ydbi.bas_s_bigdata_qut_ctl a LEFT JOIN ydbi.org_inf o ON a.dbct_cd = o.org_cd
AND a.stats_dt=DATE_SUB(#{data_date},INTERVAL 1 DAY)
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
)b ON a.stats_dt = b.stats_dt AND a.dbct_cd = b.dbct_cd LEFT JOIN( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.grtr_dist_cd when #{sel_level} = '2' then o.prov_cd when #{sel_level} = '5' then o.org_cd else '000000' end AS dbct_cd, SUM(a.dly_snd_cnt)/SUM(a.dly_tot_cnt) AS dly_rte FROM ydbi.bas_s_bigdata_qut_ctl a LEFT JOIN ydbi.org_inf o ON a.dbct_cd = o.org_cd
AND a.stats_dt=DATE_SUB(#{data_date},INTERVAL 1 WEEK)
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
)c ON a.stats_dt = c.stats_dt AND a.dbct_cd = c.dbct_cd LEFT JOIN( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.grtr_dist_cd when #{sel_level} = '2' then o.prov_cd when #{sel_level} = '5' then o.org_cd else '000000' end AS dbct_cd, SUM(a.dly_snd_cnt)/7 AS dly_snd_cnt, SUM(a.dly_snd_cnt)/SUM(a.dly_tot_cnt) AS dly_rte FROM ydbi.bas_s_bigdata_qut_ctl a LEFT JOIN ydbi.org_inf o ON a.dbct_cd = o.org_cd
AND a.stats_dt >= DATE_SUB(subdate(#{data_date},if(date_format(#{data_date},'%w')=0,7,date_format(#{data_date},'%w'))-1),INTERVAL 1 WEEK) AND a.stats_dt < subdate(#{data_date},if(date_format(#{data_date},'%w')=0,7,date_format(#{data_date},'%w'))-1)
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
)d ON a.stats_dt = d.stats_dt AND a.dbct_cd = d.dbct_cd LEFT JOIN( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.grtr_dist_cd when #{sel_level} = '2' then o.prov_cd when #{sel_level} = '5' then o.org_cd else '000000' end AS dbct_cd, SUM(a.dly_snd_cnt)/#{betweendays} AS dly_snd_cnt, SUM(a.dly_snd_cnt)/SUM(a.dly_tot_cnt) AS dly_rte FROM ydbi.bas_s_bigdata_qut_ctl a LEFT JOIN ydbi.org_inf o ON a.dbct_cd = o.org_cd
AND a.stats_dt BETWEEN #{monthstart} AND #{data_date} AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
)e ON a.stats_dt = e.stats_dt AND a.dbct_cd = e.dbct_cd

