GROUP BY 1,2 )b ON a.stats_dt = b.stats_dt AND a.dbct_cd = b.dbct_cd ORDER BY interest_rate LIMIT 0,10 ) UNION ( SELECT '疑似遗失率-全部' AS rate_flag, a.org_name AS org_name, a.dob_lost_cnt AS rate_num, Round(a.y_lost_rte*10000,2) AS interest_rate, Round((a.y_lost_rte-b.y_lost_rte)/b.y_lost_rte*100,2) AS day_percent FROM ( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.org_cd when #{sel_level} = '2' then o.org_cd when #{sel_level} = '5' then o.org_cd else o.org_cd end AS dbct_cd, case when #{sel_level} = '1' then o.org_nm when #{sel_level} = '2' then o.org_nm when #{sel_level} = '5' then o.org_nm else o.org_nm END AS org_name, SUM(a.dob_lost_cnt) AS dob_lost_cnt, Round(SUM(a.dob_lost_cnt)/SUM(a.dob_tot_cnt),8) AS y_lost_rte FROM ydbi.org_inf o LEFT JOIN ydbi.bas_s_bigdata_qut_ctl a ON a.dbct_cd = o.org_cd
AND a.stats_dt=#{data_date}
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
GROUP BY 1,2 ) a LEFT JOIN ( SELECT #{data_date} AS stats_dt, case when #{sel_level} = '1' then o.org_cd when #{sel_level} = '2' then o.org_cd when #{sel_level} = '5' then o.org_cd else o.org_cd end AS dbct_cd, SUM(a.dob_lost_cnt)/SUM(a.dob_tot_cnt) AS y_lost_rte FROM ydbi.org_inf o LEFT JOIN ydbi.bas_s_bigdata_qut_ctl a ON a.dbct_cd = o.org_cd
AND a.stats_dt=DATE_SUB(#{data_date},INTERVAL 1 DAY)
AND o.org_typ = '3' AND o.start_tm<=a.stats_dt AND o.end_tm >=a.stats_dt
AND o.grtr_dist_cd in ('1','2','3','4','5','6')
AND o.grtr_dist_cd =#{sel_cd}
AND o.prov_cd =#{sel_cd}
AND o.org_cd =#{sel_cd}
GROUP BY 1,2 )b ON a.stats_dt = b.stats_dt AND a.dbct_cd = b.dbct_cd ORDER BY interest_rate DESC LIMIT 0,1000 )
