mapper.select:

  	SELECT 
  		CONCAT(g.mc, '(', a.dbct_cd, ')') AS dbct_name,
	 	if(a.oper_typ = 0,'发出交叉带','到达交叉带') AS oper_typ,
		a.eqpt_id AS eqpt_id,a.cross_oper_cnt,
		cast(a.oper_Tot_Dur as decimal(9,2)) as  oper_Tot_Dur,
 		FORMAT(a.cross_oper_cnt / a.oper_Tot_Dur,0) AS eqpt_rate,
		cast(a.vld_Oper_Dur as decimal(9,2)) as  vld_Oper_Dur,
 		FORMAT(a.cross_oper_cnt / a.vld_Oper_Dur,0) AS itm_rate
  	FROM matrix.daw_s_cross_dbct_oper_rt a
	LEFT JOIN
		ydserver.gs g
	ON
		a.dbct_cd = g.bm



			    AND a.stats_tm >=  #{start_dt,jdbcType=DATE} 


			    AND a.stats_tm <=  #{end_dt,jdbcType=DATE}  


			    AND a.dbct_cd =  #{sel_dbct_id,jdbcType=INTEGER} 


			    AND a.eqpt_id =  #{eqpt_id,jdbcType=INTEGER} 


			    AND a.oper_typ =  #{oper_typ,jdbcType=INTEGER} 


			    AND a.dbct_cd in (
			   			select distinct
							gsa.bm as id
						from ydserver.gs gsa
						inner join ydserver.county b ON gsa.szd = b.CountyID
						inner join ydserver.city c ON b.CityID = c.CityID
						where c.ProvinceID = #{sel_cd,jdbcType=INTEGER}
						and gsa.lb = 3
			   )


	ORDER by a.dbct_cd
	LIMIT #{start_num,jdbcType=INTEGER},#{row_num,jdbcType=INTEGER}

SELECT count(*) AS dbct_cd
FROM (
SELECT CONCAT(g.mc, '(', a.dbct_cd, ')') AS dbct_name, if(a.oper_typ = 0,'发出交叉带','到达交叉带') AS oper_typ, a.eqpt_id AS eqpt_id,a.cross_oper_cnt, cast(a.oper_Tot_Dur as decimal(9,2)) as oper_Tot_Dur, FORMAT(a.cross_oper_cnt / a.oper_Tot_Dur,0) AS eqpt_rate, cast(a.vld_Oper_Dur as decimal(9,2)) as vld_Oper_Dur, FORMAT(a.cross_oper_cnt / a.vld_Oper_Dur,0) AS itm_rate FROM matrix.daw_s_cross_dbct_oper_rt a LEFT JOIN ydserver.gs g ON a.dbct_cd = g.bm
AND a.stats_tm >= #{start_dt,jdbcType=DATE}
AND a.stats_tm <= #{end_dt,jdbcType=DATE}
AND a.dbct_cd = #{sel_dbct_id,jdbcType=INTEGER}
AND a.eqpt_id = #{eqpt_id,jdbcType=INTEGER}
AND a.oper_typ = #{oper_typ,jdbcType=INTEGER}
AND a.dbct_cd in ( select distinct gsa.bm as id from ydserver.gs gsa inner join ydserver.county b ON gsa.szd = b.CountyID inner join ydserver.city c ON b.CityID = c.CityID where c.ProvinceID = #{sel_cd,jdbcType=INTEGER} and gsa.lb = 3 )
) b
