mapper.sql:

	AND k.fisc_dbct_cd =#{sel_dbct_id,jdbcType=INTEGER}


	AND k.grtr_dist_cd =#{sel_cd,jdbcType=INTEGER}


	AND k.prov_cd =#{sel_cd,jdbcType=INTEGER}


	AND k.city_cd =#{sel_cd,jdbcType=INTEGER}


	AND k.org_cd = #{sel_cd,jdbcType=INTEGER}

and a.stats_dt >= k.start_tm
and a.stats_dt < k.end_tm

mapper.select:
SELECT a.stats_dt, '0-12小时' effect_scope, FORMAT(sum(`all_len_low_12`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '12-24小时' effect_scope, FORMAT(sum(`all_len_low_24`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '24-36小时'effect_scope, FORMAT(sum(`all_len_low_36`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '36-48小时' effect_scope, FORMAT(sum(`all_len_low_48`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '48-60小时' effect_scope, FORMAT(sum(`all_len_low_60`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '60-72小时' effect_scope, FORMAT(sum(`all_len_low_72`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1 UNION ALL SELECT a.stats_dt, '大于72小时' effect_scope, FORMAT(sum(`all_len_up_72`)/sum(a.ship_cnt)*100,2) proportion FROM bas_s_bigdata_effect_start a INNER JOIN org_inf k ON a.dbct_site = k.org_cd a.stats_dt = #{curDate}
GROUP BY 1
